mapper.update:
update sys_course a, sys_clazz b set a.clazz_id=b.id
where b.grade_id=#{gradeId} and b.academic_year_semester_id=#{academicYearSemesterId}
    and a.grade_id=#{gradeId}
    and a.academic_year_semester_id=#{academicYearSemesterId}
    and concat(a.clazz_id,'班')=b.name

update sys_course a, sys_staff b set a.teacher_id=b.id
where a.grade_id=#{gradeId}
    and a.academic_year_semester_id=#{academicYearSemesterId}
    and a.teacher_name=b.name

mapper.select:
select DISTINCT sc.sort_order from sys_course sc
where  sc.sort_order not in (
    select DISTINCT sort_order from sys_course sc1  where sc1.teacher_id in (

        #{record}
    )
    and sc1.week = #{week}
    and sc.academic_year_semester_id = (
        select says.id from sys_academic_year_semester says where says.is_default = "YES" LIMIT 1
        )
)
and sc.academic_year_semester_id = (
select says.id from sys_academic_year_semester says where says.is_default = "YES" LIMIT 1
)
